import axios from 'axios';

//const apiUrl = "http://localhost:5082"
axios.defaults.baseURL = 'http://localhost:5082';
export default {
  getTasks: async () => {
    const result = await axios.get(`/items`)    
    return result.data;
  },

  addTask: async(name)=>{
    axios.interceptors.response.use(
      response => {

        return response;
      },
      error => {
        // אם קיבלת שגיאה מהשרת, עיבוד השגיאה ורישום ללוג
        console.error('An error occurred:', error);
        // החזרת השגיאה להמשך טיפול אם נדרש
        return Promise.reject(error);
      }
    );
    try{
      console.log('addTask', name)
    const result = await axios.post(`/items?name=${name}`)    
    return result.data;
    }
    catch(err){
      console.log(err);
    }
  },

  setCompleted: async(id, isComplete)=>{
    try{
      console.log('setCompleted', {id, isComplete});
    const result = await axios.put(`/items/${id}?isComplete=${isComplete}`)    
    return result.data;
    }
    catch(err){
      console.log(err);
    }
  },

  deleteTask:async(id)=>{
    console.log('deleteTask')
    const result = await axios.delete(`/items/${id}`)    
    return result.data;
  }
};
